var claim_doc_approval_obj=null;$(document).ready(function(){$(window).resize(resizeClaimDocGrid);resizeClaimDocGrid()});function resizeClaimDocGrid(){var a=$("#sbg-center-panel").outerHeight()*0.4;$("div#sbg-claim-doc-data").css("height",a+"px")}function updateClaimDoc(){saveClaimDoc(C_UPDATE)}function saveClaimDoc(b){if(validateClaimDoc()){var c={type:b,id:$("#txtClaimDocId").val(),claim_id:$("#txtClaimDocClaimId").val(),ref:$("#txtClaimDocRef").val(),desc:$("#txtClaimDocDesc").val(),path:$("#txtClaimDocPath").val()};var a="request.pzx?c="+claim_doc_approval_url+"&d="+new Date().getTime();callServer(a,"json",c,onClaimDocResponse,claim_doc_approval_obj)}}function showAllClaimDocuments(c){var b={type:C_LIST,claim_id:c};var a="request.pzx?c="+claim_doc_approval_url+"&d="+new Date().getTime();callServer(a,"json",b,onClaimDocResponse,claim_doc_approval_obj)}function onClaimDocResponse(a,b){if(b.status==C_OK){if(b.type==C_UPDATE){$($(a).closest("tr")).children("td:eq(0)").html($("#txtClaimDocId").val());$($(a).closest("tr")).children("td:eq(1)").html($("#txtClaimDocRef").val());$($(a).closest("tr")).children("td:eq(2)").html($("#txtClaimDocDesc").val());$($(a).closest("tr")).children("td:eq(3)").html($("#txtClaimDocPath").val())}else{if(b.type==C_LIST){$("tr#claim-doc-rows").remove();jQuery.each(b.data,function(){$("#sbg-claim-doc-table tr:first").after("<tr id='claim-doc-rows'><td>"+this.id+"</td><td>"+this.ref+"</td><td>"+this.desc+"</td><td>"+this.path+"</td><td style='text-align:center'><a href='javascript:' onclick='editClaimDoc("+this.id+",this)'><img src='image/edit_16.png' title='Edit'></img></a></td><td style='text-align:center'><a href='javascript:' onclick='deleteClaimDoc("+this.id+",this)'><img src='image/delete_16.png' title='Delete'></img></a></td></tr>")})}}$("#sbg-popup-box").html(b.mesg);$("#sbg-popup-box").show();$("#sbg-popup-box").fadeOut(5000)}else{showDialog("Error",b.mesg)}};