var currency_obj=null;$(document).ready(function(){$("#btnCurrencyClear").button().bind("click",clearCurrency);$("#btnCurrencyAdd").button().bind("click",addCurrency);$("#btnCurrencyUpdate").button().bind("click",updateCurrency);$("#btnCurrencyPrint").button().bind("click",printCurrency);$("#currency-tabs").tabs();$("#txtCurrencyDesc").focus();$(window).resize(resizeCurrencyGrid);resizeCurrencyGrid();clearCurrency()});function resizeCurrencyGrid(){var a=$("#sbg-center-panel").outerHeight()-$("#sbg-currency-entry").outerHeight()-55;$("div#sbg-currency-data").css("height",a+"px")}function clearCurrency(){$("#txtCurrencyId").val("Auto");$("#txtCurrencyDesc").val("");$("#txtCurrencyRef").val("");$("#currency-tabs").tabs("option","active",0);$("#txtCurrencyDesc").focus();$("#btnCurrencyUpdate").prop("disabled","disabled");$("#currency_err_mesg").text("");$("#currency_err_desc").hide();$("#currency_err_ref").hide();currency_obj=null}function updateCurrency(){saveCurrency(C_UPDATE)}function addCurrency(){saveCurrency(C_ADD)}function saveCurrency(b){if(validateCurrency()){var c={type:b,id:$("#txtCurrencyId").val(),desc:$("#txtCurrencyDesc").val(),ref:$("#txtCurrencyRef").val()};var a="request.pzx?c="+currency_url+"&d="+new Date().getTime();callServer(a,"json",c,onCurrencyResponse,currency_obj)}}function printCurrency(){var a="report.pzx?c="+currency_url+"&d="+new Date().getTime();showReport(a)}function editCurrency(d,c){var b={type:C_GET,id:d};var a="request.pzx?c="+currency_url+"&d="+new Date().getTime();currency_obj=c;callServer(a,"json",b,showCurrency,c)}function deleteCurrency(d,c){if(confirm("Confirm you want to delete currency id : "+d+"?")){var b={type:C_DELETE,id:d};var a="request.pzx?c="+currency_url+"&d="+new Date().getTime();callServer(a,"json",b,onCurrencyResponse,c)}}function showCurrency(a,b){if(b.status==C_OK){$("#txtCurrencyId").val(b.data.id);$("#txtCurrencyDesc").val(b.data.desc);$("#txtCurrencyRef").val(b.data.ref);$("#btnCurrencyUpdate").prop("disabled","");$("#txtCurrencyDesc").focus()}else{showDialog("System Message",b.mesg)}}function onCurrencyResponse(a,b){if(b.status==C_OK){if(b.type==C_ADD){$("#sbg-currency-table tr:first").after("<tr><td>"+b.data+"</td><td>"+$("#txtCurrencyDesc").val()+"</td><td style='text-align:center'><a href='javascript:' onclick='editCurrency("+b.data+",this)'><img src='image/edit_16.png' title='Edit'></img></a></td><td style='text-align:center'><a href='javascript:' onclick='deleteCurrency("+b.data+",this)'><img src='image/delete_16.png' title='Delete'></img></a></td></tr>")}else{if(b.type==C_DELETE){$($(a).closest("tr")).remove()}else{if(b.type==C_UPDATE){$($(a).closest("tr")).children("td:eq(1)").html($("#txtCurrencyDesc").val())}}}$("#sbg-popup-box").html(b.mesg);$("#sbg-popup-box").show();$("#sbg-popup-box").fadeOut(5000);clearCurrency()}else{showDialog("Error",b.mesg)}}function validateCurrency(){$("#currency_err_mesg").text("");if($("#txtCurrencyDesc").blank()){$("#currency_err_desc").show();$("#txtCurrencyDesc").focus();$("#currency_err_mesg").text("currency description can not be blank.");return false}else{$("#currency_err_desc").hide()}return true};