var bank_obj=null;$(document).ready(function(){$("#btnBankClear").button().bind("click",clearBank);$("#btnBankUpdate").button().bind("click",updateBank);$("#btnBankAdd").button().bind("click",addBank);$("#btnBankPrint").button().bind("click",printBank);$("#bank-tabs").tabs();$("#txtBankDesc").focus();clearBank();$(window).resize(resizeBankGrid);resizeBankGrid()});function resizeBankGrid(){var a=$("#sbg-center-panel").outerHeight()-$("#sbg-bank-entry").outerHeight()-55;$("div#sbg-bank-data").css("height",a+"px")}function clearBank(){$("#txtBankId").val("Auto");$("#txtBankDesc").val("");$("#txtBankRef").val("");$("#txtBankFile").val("");$("#bank-tabs").tabs("option","active",0);$("#txtBankDesc").focus();$("#btnBankUpdate").prop("disabled","disabled");$("#bank_err_mesg").text("");$("#bank_err_desc").hide();bank_obj=null}function updateBank(){saveBank(C_UPDATE)}function addBank(){saveBank(C_ADD)}function saveBank(b){if(validateBank()){var c={type:b,id:$("#txtBankId").val(),desc:$("#txtBankDesc").val(),acctno:"",refno:$("#txtBankRef").val(),file:$("#txtBankFile").val()};var a="request.pzx?c="+bank_url+"&d="+new Date().getTime();callServer(a,"json",c,onBankResponse,bank_obj)}}function printBank(){var a="report.pzx?c="+bank_url+"&d="+new Date().getTime();showReport(a)}function editBank(d,c){var b={type:C_GET,id:d};var a="request.pzx?c="+bank_url+"&d="+new Date().getTime();bank_obj=c;callServer(a,"json",b,showBank,c)}function deleteBank(d,c){if(confirm("Confirm you want to delete bank id : "+d+"?")){var b={type:C_DELETE,id:d};var a="request.pzx?c="+bank_url+"&d="+new Date().getTime();callServer(a,"json",b,onBankResponse,c)}}function showBank(a,b){if(b.status==C_OK){$("#txtBankId").val(b.data.id);$("#txtBankDesc").val(b.data.desc);$("#txtBankRef").val(b.data.refno);$("#txtBankFile").val(b.data.file);$("#txtBankDesc").focus();$("#btnBankUpdate").prop("disabled","")}else{showDialog("System Message",b.mesg)}}function onBankResponse(a,b){if(b.status==C_OK){if(b.type==C_ADD){$("#sbg-bank-table tr:first").after("<tr><td>"+b.data+"</td><td>"+$("#txtBankDesc").val()+"</td><td>"+$("#txtBankRef").val()+"</td><td style='text-align:center'><a href='javascript:' onclick='editBank("+b.data+",this)'><img src='image/edit_16.png' title='Edit'></img></a></td><td style='text-align:center'><a href='javascript:' onclick='deleteBank("+b.data+",this)'><img src='image/delete_16.png' title='Delete'></img></a></td></tr>")}else{if(b.type==C_DELETE){$($(a).closest("tr")).remove()}else{if(b.type==C_UPDATE){$($(a).closest("tr")).children("td:eq(1)").html($("#txtBankDesc").val());$($(a).closest("tr")).children("td:eq(2)").text($("#txtBankRef").val())}}}$("#sbg-popup-box").html(b.mesg);$("#sbg-popup-box").show();$("#sbg-popup-box").fadeOut(5000);clearBank()}else{showDialog("Error",b.mesg)}}function validateBank(){$("#bank_err_mesg").text("");if($("#txtBankDesc").blank()){$("#bank_err_desc").show();$("#txtBankDesc").focus();$("#bank_err_mesg").text("Bank name can not be blank.");return false}else{$("#bank_err_desc").hide()}return true};