$(document).ready(function(){$("#cobPayCreateYear").val(new Date().getFullYear());$("#cobPayCreateMonth").val(new Date().getMonth()+1);$("#btnPayCreate").button().bind("click",createPay);$("#cobPayCreateMonth").focus()});function clearPayCreate(){$("#cobPayCreateMonth").val(new Date().getMonth()+1);$("#cobPayCreateYear").val(new Date().getFullYear());$("#cobPayCreateCoy").val("");$("#cobPayCreateMonth").focus()}function createPay(){if(validatePayCreate()){var b={type:C_UPDATE,coy:$("#cobPayCreateCoy").val(),year:$("#cobPayCreateYear").val(),month:$("#cobPayCreateMonth").val()};var a="request.pzx?c="+paycreate_url+"&d="+new Date().getTime();callServer(a,"json",b,onPayCreateResponse,null)}}function onPayCreateResponse(a,b){if(b.status==C_OK){showDialog("System Message",b.mesg+"<br />No Of Employee created : "+b.data);clearPayCreate()}else{if(b.stats==C_CONFIRM){}else{showDialog("Error",b.mesg)}}}function validatePayCreate(){$("#paycreate_err_mesg").text("");return true};